import {CHANGE_SEARCH_FIELD,
    REQUEST_ROBOTS_PENDING,
    REQUEST_ROBOTS_SUCCESS,
    REQUEST_ROBOTS_FAILED} from './constants.js';


const intialSearchRobotsState = {
    searchField :'',
}

export const searchRobots = (state = intialSearchRobotsState, action={}) => {
    switch(action.type)
    {
        case CHANGE_SEARCH_FIELD:
        return Object.assign({},state, {searchField: action.payload});
        default:
        return state;
    }
}

const intialRequestRobotsState = {
    isPending: true,
    robots: [],
    error:''
}
export const requestRobots = (state = intialRequestRobotsState, action={}) => {
    switch(action.type)
    {
        case REQUEST_ROBOTS_PENDING:
        return Object.assign({},state, {isPending: true});
        case REQUEST_ROBOTS_SUCCESS:
        return Object.assign({},state, {robots: action.payload, isPending: false});
        case REQUEST_ROBOTS_FAILED:
        return Object.assign({},state, {error: action.payload, isPending:false});
        default:
        return state;
    }
}
